mapper.select:
select count(1)
from coupon_user
where coupon_template_id = #{couponTemplateId,jdbcType=VARCHAR} 

    and user_id = #{userId,jdbcType=VARCHAR}


    and coupon_status = #{couponStatus,jdbcType=VARCHAR}

        select u.coupon_no, u.trade_no, u.coupon_template_id, t.coupon_template_no, u.use_time, u.goods_id, t.start_time, t.end_time, u.user_id, t.value / 100 value
        from coupon_user as u left join coupon_template as t on u.coupon_template_id = t.coupon_template_id
        where u.coupon_status = 'used'
        
            and u.coupon_template_no = #{couponTemplateNo,jdbcType=VARCHAR}
        
        
            and u.coupon_template_id = #{couponTemplateId,jdbcType=VARCHAR}
        
        ORDER BY u.`create_time` DESC
		limit #{start},#{pageSize}
